//Spot The Difference
//You are given two strings, s and t which only consist of lowercase letters. t is generated by shuffling the letters in s as well as potentially adding an additional random character. Return the letter that was randomly added to t if it exists, otherwise, return ’ ‘.
//Note: You may assume that at most one additional character can be added to t.
//Ex: s = "foobar", t = "barfoot", return 't'
//Ex: s = "ide", t = "idea", return 'a'
//Ex: s = "coding", t "ingcod", return ''

function spotDifference(s, t){
  let sLetters = new Map();
  let tLetters = new Map();
  let differentLetter = ' ';

  for(let i = 0; i < s.length; i++){
    let currentLetter = s.charAt(i);
    if(sLetters.has(currentLetter)){
      let total = sLetters.get(currentLetter);
      sLetters.set(currentLetter, ++total);
    } else {
      sLetters.set(currentLetter, 1);
    }
  }

  for(let j = 0; j < t.length; j++){
    let currentLetter = t.charAt(j);
    if(tLetters.has(currentLetter)){
      let total = tLetters.get(currentLetter);
      tLetters.set(currentLetter, ++total);
    } else {
      tLetters.set(currentLetter, 1);
    }
  }

  tLetters.forEach((value, key) => {
    let sVal = sLetters.get(key);
    if(sVal === undefined){
      differentLetter = key;
    }
  });

  return differentLetter;
}

console.log(spotDifference('foobar', 'barfoot'));
console.log(spotDifference('ide', 'idea'));
console.log(spotDifference('coding', 'ingcod'));